import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { FiGift, FiCreditCard, FiShield, FiHeart, FiCheckCircle, FiArrowRight } from 'react-icons/fi';
import AnimatedButton from '../common/AnimatedButton';
import AnimatedCard from '../common/AnimatedCard';

const DonatePage = () => {
  const [donationAmount, setDonationAmount] = useState('');
  const [customAmount, setCustomAmount] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);

  const presetAmounts = [100, 250, 500, 1000, 2500, 5000];

  const handleDonation = async (e) => {
    e.preventDefault();
    setIsProcessing(true);
    setTimeout(() => {
      setIsProcessing(false);
      alert('Thank you for your donation! This is a demo - in a real implementation, this would integrate with a payment gateway.');
    }, 2000);
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
        ease: [0.25, 0.46, 0.45, 0.94]
      }
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Hero Section */}
      <section className="bg-gradient-to-r from-green-600 to-blue-600 text-white py-16 overflow-hidden">
        <motion.div
          animate={{ 
            rotate: 360,
            scale: [1, 1.1, 1]
          }}
          transition={{ 
            duration: 20, 
            repeat: Infinity, 
            ease: "linear" 
          }}
          className="absolute top-20 right-20 w-32 h-32 bg-white opacity-10 rounded-full"
        />
        
        <motion.div
          animate={{ 
            rotate: -360,
            scale: [1, 1.2, 1]
          }}
          transition={{ 
            duration: 25, 
            repeat: Infinity, 
            ease: "linear" 
          }}
          className="absolute bottom-20 left-20 w-24 h-24 bg-white opacity-10 rounded-full"
        />

        <div className="relative max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <motion.h1 
            className="text-4xl md:text-5xl font-bold mb-4"
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            Make a Difference Today
          </motion.h1>
          
          <motion.p 
            className="text-xl md:text-2xl mb-8 max-w-3xl mx-auto"
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            Your donation helps provide food, shelter, education, and hope to those who need it most.
          </motion.p>
          
          <motion.div 
            className="flex items-center justify-center space-x-2 text-lg"
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            <FiHeart className="w-6 h-6" />
            <span>Every rupee makes a difference</span>
          </motion.div>
        </div>
      </section>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        <motion.div 
          className="grid lg:grid-cols-3 gap-8"
          variants={containerVariants}
          initial="hidden"
          animate="visible"
        >
          {/* Donation Form */}
          <motion.div className="lg:col-span-2" variants={itemVariants}>
            <AnimatedCard className="p-8">
              <motion.h2 
                className="text-2xl font-bold text-gray-900 mb-6"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6 }}
              >
                Choose Your Donation Amount
              </motion.h2>
              
              <form onSubmit={handleDonation} className="space-y-6">
                {/* Preset Amounts */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-3">
                    Select Amount
                  </label>
                  <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
                    {presetAmounts.map((amount, index) => (
                      <motion.button
                        key={amount}
                        type="button"
                        onClick={() => setDonationAmount(amount)}
                        className={`p-4 rounded-lg border-2 font-medium transition-all duration-300 ${
                          donationAmount === amount
                            ? 'border-blue-600 bg-blue-50 text-blue-600'
                            : 'border-gray-300 hover:border-blue-400 hover:bg-blue-50'
                        }`}
                        whileHover={{ scale: 1.05, y: -2 }}
                        whileTap={{ scale: 0.95 }}
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ duration: 0.3, delay: index * 0.1 }}
                      >
                        ₹{amount.toLocaleString()}
                      </motion.button>
                    ))}
                  </div>
                </div>

                {/* Custom Amount */}
                <div>
                  <label htmlFor="custom-amount" className="block text-sm font-medium text-gray-700 mb-2">
                    Or enter custom amount
                  </label>
                  <motion.div
                    initial={{ opacity: 0, x: -20 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ duration: 0.6, delay: 0.3 }}
                  >
                    <input
                      id="custom-amount"
                      type="number"
                      value={customAmount}
                      onChange={(e) => {
                        setCustomAmount(e.target.value);
                        setDonationAmount('');
                      }}
                      placeholder="Enter amount"
                      className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300"
                    />
                  </motion.div>
                </div>

                {/* Donor Information */}
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.6, delay: 0.4 }}
                  className="space-y-4"
                >
                  <h3 className="text-lg font-semibold text-gray-900">Donor Information</h3>
                  <div className="grid md:grid-cols-2 gap-4">
                    <input
                      type="text"
                      placeholder="Full Name"
                      className="px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300"
                    />
                    <input
                      type="email"
                      placeholder="Email Address"
                      className="px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300"
                    />
                  </div>
                </motion.div>

                {/* Payment Method */}
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.6, delay: 0.5 }}
                >
                  <h3 className="text-lg font-semibold text-gray-900 mb-3">Payment Method</h3>
                  <div className="space-y-3">
                    {[
                      { icon: FiCreditCard, label: 'Credit/Debit Card', color: 'blue' },
                      { icon: FiGift, label: 'UPI', color: 'green' },
                      { icon: FiShield, label: 'Net Banking', color: 'purple' }
                    ].map((method, index) => (
                      <motion.div
                        key={method.label}
                        className="flex items-center p-4 border border-gray-300 rounded-lg hover:border-blue-400 hover:bg-blue-50 cursor-pointer transition-all duration-300"
                        whileHover={{ scale: 1.02, x: 5 }}
                        initial={{ opacity: 0, x: -20 }}
                        animate={{ opacity: 1, x: 0 }}
                        transition={{ duration: 0.3, delay: 0.6 + index * 0.1 }}
                      >
                        <method.icon className={`w-5 h-5 text-${method.color}-600 mr-3`} />
                        <span className="font-medium">{method.label}</span>
                      </motion.div>
                    ))}
                  </div>
                </motion.div>

                {/* Donation Summary */}
                <motion.div
                  className="bg-gray-50 rounded-lg p-4"
                  initial={{ opacity: 0, scale: 0.95 }}
                  animate={{ opacity: 1, scale: 1 }}
                  transition={{ duration: 0.6, delay: 0.7 }}
                >
                  <div className="flex justify-between items-center">
                    <span className="font-medium text-gray-700">Total Donation:</span>
                    <span className="text-xl font-bold text-gray-900">
                      ₹{(donationAmount || customAmount || 0).toLocaleString()}
                    </span>
                  </div>
                </motion.div>

                {/* Submit Button */}
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.6, delay: 0.8 }}
                >
                  <AnimatedButton
                    type="submit"
                    variant="primary"
                    size="lg"
                    loading={isProcessing}
                    className="w-full group"
                  >
                    <FiGift className="w-5 h-5 mr-2 group-hover:scale-110 transition-transform" />
                    <span>{isProcessing ? 'Processing...' : 'Complete Donation'}</span>
                  </AnimatedButton>
                </motion.div>
              </form>
            </AnimatedCard>
          </motion.div>

          {/* Trust Signals & Impact */}
          <motion.div className="space-y-6" variants={itemVariants}>
            {/* Trust Signals */}
            <AnimatedCard className="p-6">
              <motion.h3 
                className="text-lg font-semibold text-gray-900 mb-4 flex items-center"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6 }}
              >
                <FiShield className="w-5 h-5 text-green-600 mr-2" />
                Secure & Trusted
              </motion.h3>
              <div className="space-y-3">
                {[
                  'SSL Encrypted Transactions',
                  'PCI DSS Compliant',
                  'Tax Deductible Donations',
                  'Transparent Financial Reports'
                ].map((item, index) => (
                  <motion.div
                    key={item}
                    className="flex items-center text-sm text-gray-600"
                    initial={{ opacity: 0, x: -20 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ duration: 0.3, delay: 0.2 + index * 0.1 }}
                  >
                    <FiCheckCircle className="w-4 h-4 text-green-500 mr-2 flex-shrink-0" />
                    {item}
                  </motion.div>
                ))}
              </div>
            </AnimatedCard>

            {/* Impact Stats */}
            <AnimatedCard className="p-6">
              <motion.h3 
                className="text-lg font-semibold text-gray-900 mb-4 flex items-center"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6 }}
              >
                <FiHeart className="w-5 h-5 text-red-600 mr-2" />
                Your Impact
              </motion.h3>
              <div className="space-y-4">
                {[
                  { amount: '₹100', impact: 'Provides 2 meals' },
                  { amount: '₹500', impact: 'Supports education for 1 week' },
                  { amount: '₹1000', impact: 'Provides shelter for 1 month' },
                  { amount: '₹2500', impact: 'Funds vocational training' }
                ].map((item, index) => (
                  <motion.div
                    key={item.amount}
                    className="flex justify-between items-center p-3 bg-blue-50 rounded-lg"
                    initial={{ opacity: 0, scale: 0.95 }}
                    animate={{ opacity: 1, scale: 1 }}
                    transition={{ duration: 0.3, delay: 0.3 + index * 0.1 }}
                    whileHover={{ scale: 1.02 }}
                  >
                    <span className="font-medium text-blue-900">{item.amount}</span>
                    <span className="text-sm text-blue-700">{item.impact}</span>
                  </motion.div>
                ))}
              </div>
            </AnimatedCard>

            {/* Partner Logos */}
            <motion.div
              className="text-center p-6 bg-white rounded-lg shadow-md"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.4 }}
            >
              <h4 className="text-sm font-medium text-gray-600 mb-4">Trusted Partners</h4>
              <div className="flex justify-center space-x-6">
                {['Partner 1', 'Partner 2', 'Partner 3'].map((partner, index) => (
                  <motion.div
                    key={partner}
                    className="w-16 h-16 bg-gray-200 rounded-lg flex items-center justify-center text-xs text-gray-500"
                    whileHover={{ scale: 1.1 }}
                    initial={{ opacity: 0, scale: 0.8 }}
                    animate={{ opacity: 1, scale: 1 }}
                    transition={{ duration: 0.3, delay: 0.5 + index * 0.1 }}
                  >
                    {partner}
                  </motion.div>
                ))}
              </div>
            </motion.div>
          </motion.div>
        </motion.div>
      </div>
    </div>
  );
};

export default DonatePage;
