import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { FiHeart, FiUsers, FiBookOpen, FiHome, FiSmile, FiTarget, FiTrendingUp, FiGift, FiArrowRight } from 'react-icons/fi';
import AnimatedSection from '../common/AnimatedSection';
import AnimatedButton from '../common/AnimatedButton';
import AnimatedCard from '../common/AnimatedCard';

const HomePage = () => {
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
        ease: [0.25, 0.46, 0.45, 0.94]
      }
    }
  };

  const programs = [
    {
      icon: FiHome,
      title: "Emergency Shelter",
      description: "Safe, temporary housing for individuals and families in crisis",
      color: "indigo-blue"
    },
    {
      icon: FiBookOpen,
      title: "Education & Skills",
      description: "Literacy programs, vocational training, and life skills development",
      color: "leaf-green"
    },
    {
      icon: FiTrendingUp,
      title: "Job Placement",
      description: "Connecting individuals with employment opportunities",
      color: "bright-orange"
    },
    {
      icon: FiHeart,
      title: "Health & Wellness",
      description: "Healthcare access, mental health support, and wellness programs",
      color: "aqua-blue"
    },
    {
      icon: FiUsers,
      title: "Community Building",
      description: "Creating supportive networks and fostering social connections",
      color: "sunshine-yellow"
    },
    {
      icon: FiGift,
      title: "Food Security",
      description: "Providing nutritious meals and food assistance",
      color: "crimson-red"
    }
  ];

  const impactStats = [
    { number: "500+", label: "Meals Served Daily", color: "indigo-blue" },
    { number: "200+", label: "Shelters Provided", color: "leaf-green" },
    { number: "150+", label: "Students Supported", color: "bright-orange" },
    { number: "50+", label: "Jobs Created", color: "aqua-blue" }
  ];

  return (
    <div className="min-h-screen bg-warm-white">
      {/* Hero Section */}
      <section className="hero-gradient text-white py-16 overflow-hidden">
        <motion.div
          animate={{ 
            rotate: 360,
            scale: [1, 1.1, 1]
          }}
          transition={{ 
            duration: 20, 
            repeat: Infinity, 
            ease: "linear" 
          }}
          className="absolute top-20 right-20 w-32 h-32 bg-white opacity-10 rounded-full"
        />
        
        <motion.div
          animate={{ 
            rotate: -360,
            scale: [1, 1.2, 1]
          }}
          transition={{ 
            duration: 25, 
            repeat: Infinity, 
            ease: "linear" 
          }}
          className="absolute bottom-20 left-20 w-24 h-24 bg-white opacity-10 rounded-full"
        />

        <div className="relative max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-24">
          <motion.div
            initial={{ opacity: 0, y: 100 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1, delay: 0.2 }}
            className="text-center"
          >
            <motion.h1 
              className="text-4xl md:text-6xl font-bold mb-6"
              initial={{ opacity: 0, y: 50 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.4 }}
            >
              From Street to Stability
            </motion.h1>
            
            <motion.p 
              className="text-xl md:text-2xl mb-8 max-w-3xl mx-auto"
              initial={{ opacity: 0, y: 50 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.6 }}
            >
              Providing food, shelter, education & skills so every life thrives
            </motion.p>
            
            <motion.div 
              className="flex flex-col sm:flex-row gap-4 justify-center"
              initial={{ opacity: 0, y: 50 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.8 }}
            >
              <AnimatedButton
                variant="primary"
                size="lg"
                as={Link}
                to="/donate"
                className="group bg-bright-orange hover:bg-orange-600"
              >
                <FiGift className="w-5 h-5 mr-2 group-hover:scale-110 transition-transform" />
                <span>Donate Now</span>
              </AnimatedButton>
              
              <AnimatedButton
                variant="outline"
                size="lg"
                as={Link}
                to="/register"
                className="group bg-white text-indigo-blue hover:bg-indigo-blue hover:text-white"
              >
                <FiUsers className="w-5 h-5 mr-2 group-hover:scale-110 transition-transform" />
                <span>Get Involved</span>
              </AnimatedButton>
            </motion.div>
          </motion.div>
        </div>
      </section>

      {/* Mission Section */}
      <AnimatedSection className="py-16 bg-warm-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <motion.div 
            className="text-center mb-12"
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8 }}
          >
            <h2 className="text-3xl md:text-4xl font-bold text-charcoal-gray mb-4">Our Purpose</h2>
            <p className="text-xl text-gray-600 max-w-3xl mx-auto">
              We believe every individual deserves dignity, opportunity, and hope. Our mission is to transform lives through comprehensive support and sustainable solutions.
            </p>
          </motion.div>
          
          <motion.div 
            className="grid md:grid-cols-3 gap-8"
            variants={containerVariants}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true }}
          >
            <motion.div variants={itemVariants} className="text-center">
              <motion.div 
                className="w-16 h-16 bg-indigo-blue bg-opacity-10 rounded-full flex items-center justify-center mx-auto mb-4"
                whileHover={{ scale: 1.1, rotate: 5 }}
                transition={{ duration: 0.3 }}
              >
                <FiTarget className="w-8 h-8 text-indigo-blue" />
              </motion.div>
              <h3 className="text-xl font-semibold text-charcoal-gray mb-2">Vision</h3>
              <p className="text-gray-600">
                A world where every person has access to basic needs, education, and opportunities to build a better future.
              </p>
            </motion.div>

            <motion.div variants={itemVariants} className="text-center">
              <motion.div 
                className="w-16 h-16 bg-leaf-green bg-opacity-10 rounded-full flex items-center justify-center mx-auto mb-4"
                whileHover={{ scale: 1.1, rotate: -5 }}
                transition={{ duration: 0.3 }}
              >
                <FiHeart className="w-8 h-8 text-leaf-green" />
              </motion.div>
              <h3 className="text-xl font-semibold text-charcoal-gray mb-2">Mission</h3>
              <p className="text-gray-600">
                To provide comprehensive support and sustainable solutions that empower individuals and families.
              </p>
            </motion.div>

            <motion.div variants={itemVariants} className="text-center">
              <motion.div 
                className="w-16 h-16 bg-bright-orange bg-opacity-10 rounded-full flex items-center justify-center mx-auto mb-4"
                whileHover={{ scale: 1.1, rotate: 5 }}
                transition={{ duration: 0.3 }}
              >
                <FiSmile className="w-8 h-8 text-bright-orange" />
              </motion.div>
              <h3 className="text-xl font-semibold text-charcoal-gray mb-2">Values</h3>
              <p className="text-gray-600">
                Compassion, integrity, innovation, and collaboration guide everything we do.
              </p>
            </motion.div>
          </motion.div>
        </div>
      </AnimatedSection>

      {/* Programs Section */}
      <AnimatedSection className="py-16 bg-light-cool-gray">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <motion.div 
            className="text-center mb-12"
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8 }}
          >
            <h2 className="text-3xl md:text-4xl font-bold text-charcoal-gray mb-4">Our Programs</h2>
            <p className="text-xl text-gray-600 max-w-3xl mx-auto">
              Comprehensive support programs designed to address the root causes of poverty and create lasting change.
            </p>
          </motion.div>
          
          <motion.div 
            className="grid md:grid-cols-2 lg:grid-cols-3 gap-8"
            variants={containerVariants}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true }}
          >
            {programs.map((program, index) => {
              const IconComponent = program.icon;
              return (
                <motion.div 
                  key={program.title}
                  variants={itemVariants}
                  whileHover={{ y: -10 }}
                  transition={{ duration: 0.3 }}
                  className="bg-white rounded-lg shadow-md p-6 hover:shadow-xl transition-shadow border border-light-cool-gray"
                >
                  <motion.div 
                    className={`w-12 h-12 bg-${program.color} bg-opacity-10 rounded-lg flex items-center justify-center mb-4`}
                    whileHover={{ scale: 1.1, rotate: 5 }}
                    transition={{ duration: 0.3 }}
                  >
                    <IconComponent className={`w-6 h-6 text-${program.color}`} />
                  </motion.div>
                  <h3 className="text-lg font-semibold text-charcoal-gray mb-2">{program.title}</h3>
                  <p className="text-gray-600 mb-4">{program.description}</p>
                  <motion.div
                    whileHover={{ x: 5 }}
                    transition={{ duration: 0.2 }}
                  >
                    <Link 
                      to="/programs" 
                      className="inline-flex items-center text-indigo-blue hover:text-indigo-700 font-medium"
                    >
                      Learn More <FiArrowRight className="w-4 h-4 ml-1" />
                    </Link>
                  </motion.div>
                </motion.div>
              );
            })}
          </motion.div>
        </div>
      </AnimatedSection>

      {/* Impact Section */}
      <AnimatedSection className="py-16 bg-warm-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <motion.div 
            className="text-center mb-12"
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8 }}
          >
            <h2 className="text-3xl md:text-4xl font-bold text-charcoal-gray mb-4">Our Impact</h2>
            <p className="text-xl text-gray-600 max-w-3xl mx-auto">
              Real stories of transformation and the measurable difference we're making in communities.
            </p>
          </motion.div>
          
          <div className="grid md:grid-cols-2 gap-8 mb-12">
            <motion.div 
              className="bg-light-cool-gray rounded-lg p-8"
              initial={{ opacity: 0, x: -50 }}
              whileInView={{ opacity: 1, x: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.8 }}
            >
              <h3 className="text-2xl font-bold text-charcoal-gray mb-4">Before & After</h3>
              <div className="space-y-4">
                <motion.div 
                  className="flex items-center space-x-4"
                  whileHover={{ x: 10 }}
                  transition={{ duration: 0.2 }}
                >
                  <div className="w-3 h-3 bg-crimson-red rounded-full"></div>
                  <span className="text-gray-600">Begging on streets</span>
                  <FiArrowRight className="w-4 h-4 text-gray-400" />
                  <span className="text-charcoal-gray font-medium">Skill-based work</span>
                </motion.div>
                <motion.div 
                  className="flex items-center space-x-4"
                  whileHover={{ x: 10 }}
                  transition={{ duration: 0.2 }}
                >
                  <div className="w-3 h-3 bg-crimson-red rounded-full"></div>
                  <span className="text-gray-600">₹0/day income</span>
                  <FiArrowRight className="w-4 h-4 text-gray-400" />
                  <span className="text-charcoal-gray font-medium">₹10,000+/month</span>
                </motion.div>
                <motion.div 
                  className="flex items-center space-x-4"
                  whileHover={{ x: 10 }}
                  transition={{ duration: 0.2 }}
                >
                  <div className="w-3 h-3 bg-crimson-red rounded-full"></div>
                  <span className="text-gray-600">No education</span>
                  <FiArrowRight className="w-4 h-4 text-gray-400" />
                  <span className="text-charcoal-gray font-medium">Computer skills</span>
                </motion.div>
              </div>
            </motion.div>

            <motion.div 
              className="bg-light-cool-gray rounded-lg p-8"
              initial={{ opacity: 0, x: 50 }}
              whileInView={{ opacity: 1, x: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.8 }}
            >
              <h3 className="text-2xl font-bold text-charcoal-gray mb-4">Quick Facts</h3>
              <div className="grid grid-cols-2 gap-4">
                {impactStats.map((stat, index) => (
                  <motion.div 
                    key={stat.label}
                    className="text-center"
                    initial={{ opacity: 0, scale: 0.8 }}
                    whileInView={{ opacity: 1, scale: 1 }}
                    viewport={{ once: true }}
                    transition={{ duration: 0.5, delay: index * 0.1 }}
                    whileHover={{ scale: 1.05 }}
                  >
                    <div className={`text-3xl font-bold text-${stat.color} mb-1`}>
                      {stat.number}
                    </div>
                    <div className="text-sm text-gray-600">{stat.label}</div>
                  </motion.div>
                ))}
              </div>
            </motion.div>
          </div>
          
          <motion.div 
            className="text-center"
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8 }}
          >
            <AnimatedButton
              variant="primary"
              size="lg"
              as={Link}
              to="/stories"
              className="group bg-bright-orange hover:bg-orange-600"
            >
              <FiHeart className="w-5 h-5 mr-2 group-hover:scale-110 transition-transform" />
              <span>Read More Stories</span>
            </AnimatedButton>
          </motion.div>
        </div>
      </AnimatedSection>

      {/* CTA Section */}
      <AnimatedSection className="py-16 accent-gradient text-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <motion.h2 
            className="text-3xl md:text-4xl font-bold mb-4"
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8 }}
          >
            Join Us in Making a Difference
          </motion.h2>
          
          <motion.p 
            className="text-xl mb-8 max-w-2xl mx-auto"
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            Every contribution, whether time, skills, or resources, helps us create lasting positive change in our community.
          </motion.p>
          
          <motion.div 
            className="flex flex-col sm:flex-row gap-4 justify-center"
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            <AnimatedButton
              variant="outline"
              size="lg"
              as={Link}
              to="/register"
              className="group bg-white text-indigo-blue hover:bg-indigo-blue hover:text-white"
            >
              <FiUsers className="w-5 h-5 mr-2 group-hover:scale-110 transition-transform" />
              <span>Become a Volunteer</span>
            </AnimatedButton>
            
            <AnimatedButton
              variant="primary"
              size="lg"
              as={Link}
              to="/donate"
              className="group bg-bright-orange hover:bg-orange-600"
            >
              <FiGift className="w-5 h-5 mr-2 group-hover:scale-110 transition-transform" />
              <span>Make a Donation</span>
            </AnimatedButton>
          </motion.div>
        </div>
      </AnimatedSection>
    </div>
  );
};

export default HomePage;
